Rake = RAKE.Rake(RAKE.SmartStopList())

authors = ['John Powers (academic)','Tom Shroder','Anthony Doerr','Plato','Atul Gawande','Toni Morrison','Aldous Huxley','Epictetus','Plato','Dalai Lama XIV','Maya Angelou','Jean-Paul Sartre','David Grossman','Chuck Palahniuk','Oliver Sacks','Jon Krakauer','Jon Krakauer','Jules Verne','Varlam Shalamov','Plato','William Shakespeare','Marcus Aurelius','Jack Spicer','Jane Austen','Kurt Vonnegut Jr.','Alan Lightman','Paulo Coelho','Thanissaro Bhikkhu','Gretchen Morgenson','Czeslaw Milosz','Barry M. Goldwater','Paul Krugman','Mitch Albom','Stephen Hawking','Maylis de Kerangal','Khaled Hosseini','Thomas Mann','Stefan Hildebrandt','Ernest Hemingway','Albert Camus','Ernest Hemingway','Plato','V.S. Ramachandran','Tadeusz Borowski','Paul Kalanithi']

cloud = []
